INSERT INTO standard (standard_id, name, description, creation_date, modification_date) VALUES
('ISO 3103', 'Standardized method for brewing tea', 'The method consists in extracting of soluble substances in dried tea leaf, contained in a porcelain or earthenware pot, by means of freshly boiling water, pouring of the liquor into a white porcelain or earthenware bowl, examination of the organoleptic properties of the infused leaf, and of the liquor with or without milk, or both.', '2021-05-24T09:31:10.123', '2021-05-24T09:32:11.321');
